mapper.update:
UPDATE schedule
set
time_start = #{timeStart},
time_end = #{timeEnd}
WHERE		
id = #{id}

mapper.select:
SELECT * FROM schedule
WHERE
	del_flag = 0

		AND id = #{id}


		AND user_id = #{userId}


		AND service_id = #{serviceId}


		AND car_id = #{carId}


		AND time_start >= #{timeStart,jdbcType=TIMESTAMP}


		AND time_end <= #{timeEnd,jdbcType=TIMESTAMP}

SELECT scp.price AS price FROM user_car usc LEFT 
JOIN service_car_price scp ON (scp.car_type_id = usc.car_type_id) 
WHERE
	scp.del_flag = 0

		AND scp.service_id = #{serviceId}


		AND usc.id = #{carId}

SELECT sch.id AS id, usr.name AS user_name, cat.name AS car_type, ser.name AS service_name, sch.price AS price, sch.time_start AS time_start, sch.time_end AS time_end FROM schedule sch LEFT JOIN user usr ON sch.user_id = usr.id LEFT JOIN user_car usc ON sch.car_id = usc.id LEFT JOIN service ser ON sch.service_id = ser.id LEFT JOIN car_type cat ON usc.car_type_id = cat.id WHERE sch.del_flag = 0
AND sch.id = #{id}
AND sch.user_id = #{userId}

SELECT sch.id AS id, usr.name AS user_name, cat.name AS car_type, ser.name AS service_name, sch.price AS price, sch.time_start AS time_start, sch.time_end AS time_end FROM schedule sch LEFT JOIN user usr ON sch.user_id = usr.id LEFT JOIN user_car usc ON sch.car_id = usc.id LEFT JOIN service ser ON sch.service_id = ser.id LEFT JOIN car_type cat ON usc.car_type_id = cat.id WHERE sch.del_flag = 0
AND usr.name = #{userName}
AND usc.car_type_id = #{carTypeId}
AND sch.service_id = #{serviceId}
AND sch.time_start >= #{timeStart,jdbcType=TIMESTAMP}
AND sch.time_end <= #{timeEnd,jdbcType=TIMESTAMP}
